def val = testRunner.testCase.testSteps['Login'].testRequest.response.getResponseHeaders()
loggedInValue = "true"

Map<String,List> values  = new HashMap<String,String>();

for(int j = 0; j < val.get("Set-Cookie").size(); j++)
{

	def cjar = val.get("Set-Cookie")[j]
	def cookies = cjar.tokenize("\\;")

	cookies.each() 
	{ cookie ->
    	def pair = cookie.tokenize("\\=")
    	values.put(pair[0], pair[1])

    		log.info( "Key: " + pair[0] + ", Val: " + pair[1] )
	}
}

		values.keySet()
		if (values.get("SESSION") == null)
		{
			loggedInValue = "false"
		}
		log.info("LOGGED IN: " + loggedInValue)
		testRunner.testCase.testSuite.project.setPropertyValue("loggedIn", loggedInValue)
		testRunner.testCase.testSuite.project.setPropertyValue( 'token', values.get("XSRF-TOKEN"))
		testRunner.testCase.testSuite.project.setPropertyValue( 'cookie', "SESSION="+values.get("SESSION" ))

		//Generate TransactionID
		//def random = (int)(Math.random()*1000);
          //testRunner.testCase.testSuite.project.setPropertyValue("RandomID", String.valueOf((int)(Math.random()*100000)) );

import groovy. json.*
import java.io.*;
import java.util.Iterator;
		def response=context.expand('${get_user_list#Response}')		
		def json = new JsonSlurper().parseText(response)
		currentUserId = json[0]['userId']
		//log.info currentEntityID
		log.info(currentUserId.toString())
		testRunner.testCase.testSuite.project.setPropertyValue('currentUserId', currentUserId.toString())

import groovy. json.*
import java.io.*;
import java.util.Iterator;

		//get and parse response
		def response=context.expand('${get_SIEM_info#Response}')		
		def json = new JsonSlurper().parseText(response)

		//number of SIEM configurations that exist
		siemNum = json['name'].size()
		log.info(siemNum)
		int i;
		//iterate through all configs to find the one that was created
		for(i = 0; i<siemNum; i++){
			log.info(json['name'][i])
			if(json['name'][i].equals("APITEST")){
				log.info(json[i]['siemconfigId'])//].toString());
				testRunner.testCase.testSuite.project.setPropertyValue("siemId", json[i]['siemconfigId'].toString());
				break;
			}

		}
		//iterated through all and didn't find
		if(i == siemNum){
			log.info("iterated through all and didn't find")
			return false;
		}

		//check correct properties
		else{
			port = json[i]['port'].toString().equals("777");
               desc = json[i]['description'].toString().equals("test");
			ip = json[i]['ip'].toString().equals("10.50.50.179");

			log.info("port=" + port.toString() +" desc="+desc.toString() + "  ip="+ip.toString())

			//true only if all properties match
			if(port && desc && ip){
				return true;
			}
		}
		//incorrect property found
		return false;

