import groovy. json.*
import java.io.*;
import java.util.Iterator;

		//get and parse response
		def response=context.expand('${get_list_to_validate2#Response}')		
		def json = new JsonSlurper().parseText(response)
		size = json.size();

		//set the rule name
		def ruleName ="MultipleLoginsDifferentUsersXUA"
		log.info("Name = " + ruleName)
		testRunner.testCase.testSuite.project.setPropertyValue("ruleName", ruleName);

		//look through all rules
		for(int i = 0; i < size; i++){

			//if a configurable rule is found
			if(json[i]['name'].equals(ruleName)){
				log.info(i);
				log.info(json[i]['ruleId'].toString());

				//save location of rule
				testRunner.testCase.testSuite.project.setPropertyValue("ruleLocation", i.toString());
				testRunner.testCase.testSuite.project.setPropertyValue("currentRuleUpdateID", json[i]['ruleId'].toString());

			}
		}

import groovy. json.*
import java.io.*;
import java.util.Iterator;

		//get and parse response
		def response=context.expand('${get_list_to_validate3#Response}')		
		def json = new JsonSlurper().parseText(response)
		def location = testRunner.testCase.testSuite.project.getPropertyValue("ruleLocation").toInteger();
		def ruleName = testRunner.testCase.testSuite.project.getPropertyValue("ruleName");
		log.info(location);
		log.info(ruleName)

		if(!json[location]["name"].equals(ruleName)){
			log.info("Rule name at location doesn't match expected output")
			return false;
		}
		//check for correct information
		withinMinutes = (json[location]['displayPattern']['pattern'][1]['minutes'] == 7);
		log.info(withinMinutes)
		repeatCount = (json[location]['displayPattern']['pattern'][2]['value'] == 1);
		log.info(repeatCount)
		repeatFor = (json[location]['displayPattern']['pattern'][2]['until']['minutes'] == 6);
		log.info(repeatFor)
		return(withinMinutes && repeatCount && repeatFor);

import groovy. json.*
import java.io.*;
import java.util.Iterator;

		//get and parse response
		def response=context.expand('${get_list_to_validate4#Response}')		
		def json = new JsonSlurper().parseText(response)
		def location = testRunner.testCase.testSuite.project.getPropertyValue("ruleLocation").toInteger();
		def ruleName = testRunner.testCase.testSuite.project.getPropertyValue("ruleName");
		log.info(location);
		if(!json[location]["name"].equals(ruleName)){
			log.info("Rule name at location doesn't match expected output")
			return false;
		}
		minutes = (json[location]['displayPattern']['pattern'][1]['minutes'] == 5);
		repeatEvery = (json[location]['displayPattern']['pattern'][2]['value'] == 0);
		repeatFor = (json[location]['displayPattern']['pattern'][2]['until']['minutes'] == 5);
		return(minutes && repeatEvery && repeatFor);

import groovy. json.*
import java.io.*;
import org.apache.poi.hssf.usermodel.HSSFWorkbook;
import org.apache.poi.hssf.usermodel.HSSFSheet;
import org.apache.poi.hssf.usermodel.HSSFRow;
import org.apache.poi.ss.usermodel.*;
import org.apache.poi.hssf.usermodel.*;


		def projectDirectory = context.expand('${projectDir}')
		def fileName = projectDirectory + testRunner.testCase.testSuite.project.getPropertyValue("reportsFilename")
          log.info(fileName)

		File file = new File(fileName)

		FileInputStream fsIP= new FileInputStream(file); //Read the spreadsheet that needs to be updated
          HSSFWorkbook wb = new HSSFWorkbook(fsIP); //Access the workbook
          HSSFSheet worksheet = wb.getSheetAt(0); //Access the worksheet, so that we can update / modify it.

		String[] steps = ["get_rule_patterns", "get_incidents_list", "get_rule_definitions", "deactivate_rule", 
		"update_rule_severity_critical", "update_when_triggered_don't_send_an_email", "get_list_to_validate_1", "update_rule_severity_major", "update_when_triggered_send_an_email",
		"update_rule_severity_major", "update_when_triggered_send_an_email", "restore_default_rule_param", "get_incident_graph", "get_incident_graph_ruleid",  "activate_rule",  "update_rule_params"];

		//get all steps from Excel
          HashMap<String,Integer> stepsMap=new HashMap<String,Integer>();  
          for(int i = 1; i <= worksheet.getLastRowNum() ; i++){
          	HSSFRow row = worksheet.getRow(i);
			cell = row.getCell(0);
			def stepName = cell.toString();
			stepsMap.put(stepName,i);  		

		}


		Cell cell = null; // declare a Cell object
		for(int i = 0; i < steps.length; i++){
			def currentStepName = steps[i];
			currentStepIndex = stepsMap.get(currentStepName);
			if (currentStepIndex!=null){
				def currentTestStep = testRunner.testCase.testSteps[currentStepName];
				if (currentTestStep == null){
					log.info("NOT FOUND PROJECT STEP " + currentStepName.toString());
				}
				else{
					def currentTestStepResponse = currentTestStep.testRequest.response;
					if (currentStepIndex != null && currentTestStepResponse != null){
						def response = currentTestStep.testRequest.response.timeTaken;
						log.info(response)
						HSSFRow row = worksheet.getRow(currentStepIndex);
						cell = row.getCell(9);
						cell.setCellValue(response);
					}
					else{
						log.info("NOT FOUND TIMETAKEN FOR " + currentStepName.toString());
					}
				}
			}
			else{
				log.info("NOT FOUND STEP IN EXCEL " + currentStepName.toString());
			}
		}


		fsIP.close(); //Close the InputStream
		FileOutputStream output_file =new FileOutputStream(file);  //Open FileOutputStream to write updates
          wb.write(output_file); //write changes
          output_file.close();  //close the stream
