con:config.script:
def val = testRunner.testCase.testSteps['Login'].testRequest.response.getResponseHeaders()
//log.info(val)
loggedInValue = "true"

Map<String,List> values  = new HashMap<String,String>();

for(int j = 0; j < val.get("Set-Cookie").size(); j++)
{

	def cjar = val.get("Set-Cookie")[j]
	def cookies = cjar.tokenize("\\;")

	cookies.each() 
	{ cookie ->
    	def pair = cookie.tokenize("\\=")
    	values.put(pair[0], pair[1])

    		log.info( "Key: " + pair[0] + ", Val: " + pair[1] )
	}
}

		values.keySet()
		if (values.get("SESSION") == null)
		{
			loggedInValue = "false"
		}
		log.info("LOGGED IN: " + loggedInValue)
		testRunner.testCase.testSuite.project.setPropertyValue("loggedIn", loggedInValue)
		testRunner.testCase.testSuite.project.setPropertyValue( 'token', values.get("XSRF-TOKEN"))
		testRunner.testCase.testSuite.project.setPropertyValue( 'cookie', "SESSION="+values.get("SESSION" ))

		//Generate TransactionID
		//def random = (int)(Math.random()*1000);
          //testRunner.testCase.testSuite.project.setPropertyValue("RandomID", String.valueOf((int)(Math.random()*100000)) );

import groovy. json.*
import java.io.*;
import java.util.Iterator;
		def response=context.expand('${create_monitor#Response}')		
		def json = new JsonSlurper().parseText(response)
		currentID = json['monitor']['monitorId']
		//log.info currentID
		log.info( 'monitors-' +currentID.toString())
		testRunner.testCase.testSuite.setPropertyValue( 'currentMonitorID', currentID.toString())
		testRunner.testCase.testSuite.setPropertyValue( 'monitorIDView', 'monitors-' +currentID.toString())
		testRunner.testCase.testSuite.setPropertyValue( 'monitorGridView', 'alerts-' +currentID.toString())

import groovy. json.*
import java.io.*;
import java.util.Iterator;
		def response=context.expand('${get_monitor_list#Response}')		
		def json = new JsonSlurper().parseText(response)
		def length = json.size()
		log.info(length)
		for(i = 0; i<length; i++){
				log.info(json[i]['monitorName'])
			if(json[i]['monitorName'].equals("ApiUserMonitor1")){
				log.info(json[i])
				return true;

			}

		}
		return false;
		//itera

