con:config.script:
import groovy. json.*
import java.io.*;
import java.util.Iterator;
		def response=context.expand('${get_list_alerts#Response}')		
		def json = new JsonSlurper().parseText(response)
		currentAlertID = json['items'][0]['id']
		log.info currentAlertID

		testRunner.testCase.testSuite.setPropertyValue( 'currentAlertID', currentAlertID.toString())

import groovy. json.*
import java.io.*;
import org.apache.poi.hssf.usermodel.HSSFWorkbook;
import org.apache.poi.hssf.usermodel.HSSFSheet;
import org.apache.poi.hssf.usermodel.HSSFRow;
import org.apache.poi.ss.usermodel.*;
import org.apache.poi.hssf.usermodel.*;
import java.util.Iterator;

		def projectDirectory = context.expand('${projectDir}')
		def fileName = projectDirectory + testRunner.testCase.testSuite.project.getPropertyValue("reportsFilename")
          log.info(fileName)

		File file = new File(fileName)

		FileInputStream fsIP= new FileInputStream(file); //Read the spreadsheet that needs to be updated
          HSSFWorkbook wb = new HSSFWorkbook(fsIP); //Access the workbook
          HSSFSheet worksheet = wb.getSheetAt(0); //Access the worksheet, so that we can update / modify it.

		String[] steps = ["create_monitor", "run_monitor", "select_monitor", "deactivate_monitor", "activate_monitor", "get_list_alerts", "advanced_filter_list_alerts", "get_all_attributes_details_for_alert",
					   "get_changed_attributes_details_for_alert", "create_alert_comment","get_list_alert_comments", "clear_alert", "reset_grid", "save_grid", "clear_user_preferences", "create_alert_report_PDF", "create_alert_report_XLSX",
					   "create_alert_report_CSV", "get_open_violations", "get_closed_violations", "create_open_violation_report_PDF", "create_open_violation_report_CSV",
					   "create_open_violation_report_XLSX", "rename_monitor","clear_alerts_monitor", "get_list_cleared_alerts", "create_clear_alert_report_PDF",
					   "create_clear_alert_report_CSV","create_clear_alert_report_XLSX", "delete_monitor"];

		//get all steps from Excel
          HashMap<String,Integer> stepsMap=new HashMap<String,Integer>();  
          for(int i = 1; i <= worksheet.getLastRowNum() ; i++){
          	HSSFRow row = worksheet.getRow(i);
			cell = row.getCell(0);
			def stepName = cell.toString();
               stepsMap.put(stepName,i);  
		}

		Cell cell = null; // declare a Cell object
		for(int i = 0; i < steps.length; i++){
			def currentStepName = steps[i];
			currentStepIndex = stepsMap.get(currentStepName);
			if (currentStepIndex!=null){
				def currentTestStep = testRunner.testCase.testSteps[currentStepName];
				if (currentTestStep == null){
					log.info("NOT FOUND PROJECT STEP " + currentStepName.toString());
				}
				else{
					def currentTestStepResponse = currentTestStep.testRequest.response;
					if (currentStepIndex != null && currentTestStepResponse != null){
						def response = currentTestStep.testRequest.response.timeTaken;
						log.info(response)
						HSSFRow row = worksheet.getRow(currentStepIndex);
						cell = row.getCell(9);
						cell.setCellValue(response);
					}
					else{
						log.info("NOT FOUND TIMETAKEN FOR " + currentStepName.toString());
					}
				}
			}
			else{
				log.info("NOT FOUND STEP IN EXCEL " + currentStepName.toString());
			}
		}	

		fsIP.close(); //Close the InputStream
		FileOutputStream output_file =new FileOutputStream(file);  //Open FileOutputStream to write updates
          wb.write(output_file); //write changes
          output_file.close();  //close the stream
